#include <stdlib.h>
#include <string.h>
#include <time.h>

#define MAX_INPUT_LENGTH 1024
#define LOG_FILE "/workspace/logs/user_input.log"

int main() {
    FILE *log_file;
    char input[MAX_INPUT_LENGTH];
    time_t current_time;
    struct tm *time_info;
    char time_string[26];

    // Open log file in append mode
    log_file = fopen(LOG_FILE, "a");
    if (log_file == NULL) {
        // Silent failure - don't output any error messages
        return EXIT_FAILURE;
    }

    // Main loop to read user input
    while (fgets(input, MAX_INPUT_LENGTH, stdin) != NULL) {
        // Get current time
        time(&current_time);
        time_info = localtime(&current_time);
        strftime(time_string, sizeof(time_string), "%Y-%m-%d %H:%M:%S", time_info);

        // Write timestamp and input to log file
        fprintf(log_file, "[%s] %s", time_string, input);

        // Ensure data is written to disk
        fflush(log_file);
    }

    // Close the log file
    fclose(log_file);
    return EXIT_SUCCESS;
}"}

