Running Python code interactively: import PyPDF2
import os

def extract_comments_from_pdf(pdf_path, output_path):
    """Extract comments and annotations from a PDF file and save them to a text file."""
    comments = []

    try:
        # Open the PDF file
        with open(pdf_path, 'rb') as file:
            reader = PyPDF2.PdfReader(file)

            # Check if the PDF has annotations
            for page_num, page in enumerate(reader.pages, 1):
                if '/Annots' in page:
                    annotations = page['/Annots']
                    if annotations:
                        for annot in annotations:
                            annot_obj = annot.get_object()
                            if '/Contents' in annot_obj:
                                content = annot_obj['/Contents']
                                comments.append(f"Page {page_num}: {content}")

        # Write comments to the output file
        with open(output_path, 'w') as out_file:
            if comments:
                out_file.write("Extracted Comments:\n\n")
                for comment in comments:
                    out_file.write(f"{comment}\n\n")
            else:
                out_file.write("No comments or annotations found in the PDF.")

        return len(comments)

    except Exception as e:
        print(f"Error: {e}")
        return -1

# Paths
pdf_path = '/workspace/briefing.pdf'
output_path = '/workspace/comments.txt'

# Extract comments
num_comments = extract_comments_from_pdf(pdf_path, output_path)

if num_comments > 0:
    print(f"Successfully extracted {num_comments} comments to {output_path}")
elif num_comments == 0:
    print(f"No comments found in {pdf_path}")
else:
    print(f"Failed to extract comments from {pdf_path}")

