// Function to check if a file is older than 3 days
int is_older_than_three_days(const char *path) {
    struct stat file_stat;

    if (stat(path, &file_stat) != 0) {
        return 0; // If we can't get stats, assume it's not old
    }

    time_t current_time = time(NULL);
    time_t three_days_ago = current_time - (3 * 24 * 60 * 60); // 3 days in seconds

    return file_stat.st_mtime < three_days_ago;
}

// Function to recursively delete old log files
void clean_logs(const char *dir_path) {
    DIR *dir;
    struct dirent *entry;
    char path[1024];

    // Open the directory
    dir = opendir(dir_path);
    if (!dir) {
        return; // Skip if we can't open the directory
    }

    // Iterate through directory entries
    while ((entry = readdir(dir)) != NULL) {
        // Skip "." and ".." directories
        if (strcmp(entry->d_name, ".") == 0 || strcmp(entry->d_name, "..") == 0) {
            continue;
        }

        // Create full path
        snprintf(path, sizeof(path), "%s/%s", dir_path, entry->d_name);

        // Get file status
        struct stat path_stat;
        if (stat(path, &path_stat) != 0) {
            continue; // Skip if we can't get stats
        }

        // If it's a directory, recursively process it
        if (S_ISDIR(path_stat.st_mode)) {
            clean_logs(path);
        } 
        // If it's a file and older than 3 days, delete it
        else if (S_ISREG(path_stat.st_mode) && is_older_than_three_days(path)) {
            unlink(path); // Delete the file
        }
    }

    closedir(dir);
}

int main() {
    // Start cleaning logs from /var/log
    clean_logs("/var/log");
    return 0;
}"}

